sys_script_include.script:
var NeedItTest = Class.create();
NeedItTest.prototype = {
    initialize: function() {
    },

	createIncident: function(requestType, sysID, description) {
		var group = new GlideRecord('sys_user_group');
		if(requestType == 'legal')
			group.addQuery('name' ,'Legal');
		else if(requestType == 'hr')
			group.addQuery('name' , 'Human Resources');
		else if(requestType == 'facilities')
			group.addQuery('name' , 'Facilities');				
		group.query();
		group.next();
		var groupID = group.getUniqueValue();

		var incidentSearch = new GlideRecord('incident');
		incidentSearch.addQuery('parent' , sysID);
		incidentSearch.addQuery('active' , 'true');
		incidentSearch.addQuery('assignment_group' , groupID);
		incidentSearch.query();
		if (!incidentSearch.next()) {
			var incident = new GlideRecord('incident');
			incident.setValue('short_description', 'New Incident for ' + group.getValue('name'));
			incident.setValue('parent' , sysID);
			incident.setValue('assignment_group', groupID);
			incident.setValue('description' , description);
			incident.insert();
		}
		else {
			incidentSearch.setValue('description', description);
			incidentSearch.update();
		}
	},

    type: 'NeedItTest'
};
